import React, { Component } from "react";

import {
  FormGroup,
  Form,
  Row,
  Col,
  FormControl,
  Button,
  ControlLabel,
  HelpBlock,
  Glyphicon,
  DropdownButton,
  MenuItem,
  Image,
  Media,
  Navbar,
  Nav,
  NavItem,
  NavDropdown
} from "react-bootstrap";
import Game from "./game";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      response: "",
      value: "",
      isEdit: false,
      isSignUp: true,
      isLogin: false,
      label: {
        lang: "Language",
        name: "Name",
        user: "Username",
        skills: "Skills",
        gender: "Gender",
        male: "Male",
        female: "Female",
        fb: "Facebook",
        privacy: "Privacy",
        public: "Public",
        private: "Private",
        link: "Link your social",
        save: "Save"
      }
    };
  }

  componentDidMount() {
    this.callApi()
      .then(res => this.setState({ response: res.express }))
      .catch(err => console.log(err));
  }

  getValidationState() {
    const length = this.state.value.length;
    if (length > 10) return "success";
    else if (length > 5) return "warning";
    else if (length > 0) return "error";
    return null;
  }

  handlePageChange(page) {
    if (page === "game") {
      this.setState({ isEdit: false, isSignUp: false });
    }
    if (page === "edit") {
      this.setState({ isEdit: true, isSignUp: false });
    }
    if (page === "signup") {
      this.setState({ isEdit: false, isSignUp: true });
    }
  }

  handleChange(e) {
    this.setState({ value: e.target.value });
  }
  async callApi () {
    const response = await fetch("http://localhost:5000/api/v1/user");
    const body = await response.json();

    if (response.status !== 200) throw Error(body.message);

    return body;
  };

  render() {
    const { isSignUp, isEdit, isLogin } = this.state;
    return (
      <div className="App">
        <header className="App-Header">
          <Navbar>
            <Navbar.Header>
              <Navbar.Brand>
                <a href="#home">Ocd - Cards</a>
              </Navbar.Brand>
            </Navbar.Header>
            {isLogin ? (
              <Nav>
                <NavItem onClick={() => this.handlePageChange("game")} href="#">
                  Game
                </NavItem>
                <NavItem onClick={() => this.handlePageChange("edit")} href="#">
                  Edit
                </NavItem>
                <NavItem
                  onClick={() => this.handlePageChange("logout")}
                  href="#"
                >
                  logout
                </NavItem>
              </Nav>
            ) : (
              <Nav>
                <NavItem
                  onClick={() => this.handlePageChange("signup")}
                  href="#"
                >
                  Sign Up
                </NavItem>
              </Nav>
            )}
          </Navbar>
        </header>
        <div className="App-Body">
          {isSignUp || isEdit ? (
            <Form
              onSubmit={() => {
                this.callApi;
              }}
            >
              <Row className="show-grid">
                <Col xs={6} md={6}>
                  <FormGroup controlId="formBasicText">
                    <ControlLabel>{this.state.label.name}</ControlLabel>

                    <FormControl
                      type="text"
                      value={this.state.value}
                      placeholder="Enter your name"
                      onChange={this.handleChange}
                    />
                    <FormControl.Feedback />
                  </FormGroup>
                </Col>
                <Col xs={6} md={6}>
                  <FormGroup controlId="formBasicText">
                    <ControlLabel>{this.state.label.user}</ControlLabel>
                    <FormControl
                      type="text"
                      value={this.state.value}
                      placeholder="enter your username"
                      onChange={this.handleChange}
                    />
                    <FormControl.Feedback />
                  </FormGroup>
                </Col>
              </Row>

              <FormGroup controlId="formControlsTextarea">
                <ControlLabel>{this.state.label.bio}</ControlLabel>
                <FormControl componentClass="textarea" placeholder="textarea" />
              </FormGroup>

              <Row className="show-grid">
                <Col xs={6} md={6}>
                  <FormGroup controlId="formControlsSelect">
                    <ControlLabel>{this.state.label.gender}</ControlLabel>
                    <FormControl componentClass="select" placeholder="Male">
                      <option value="male">{this.state.label.male}</option>
                      <option value="female">{this.state.label.female}</option>
                    </FormControl>
                  </FormGroup>
                </Col>

                <Col xs={6} md={6}>
                  <FormGroup controlId="formControlsSelect">
                    <ControlLabel>{this.state.label.privacy}</ControlLabel>
                    <FormControl componentClass="select" placeholder="Public">
                      <option value="public">{this.state.label.public}</option>
                      <option value="private">
                        {this.state.label.private}
                      </option>
                    </FormControl>
                  </FormGroup>
                </Col>
              </Row>

              <ControlLabel>{this.state.label.link}</ControlLabel>
              <Row className="show-grid">
                <Col xs={6} md={6}>
                  <FormGroup
                    controlId="formValidationSuccess1"
                    validationState="null"
                  >
                    <FormControl
                      type="text"
                      className="vk"
                      placeholder={this.state.label.vk}
                    />
                  </FormGroup>
                </Col>
                <Col xs={6} md={6}>
                  <FormGroup controlId="formValidationSuccess2">
                    <FormControl
                      type="text"
                      className="fb"
                      placeholder={this.state.label.fb}
                    />
                  </FormGroup>
                </Col>
              </Row>

              <Button bsStyle="custom" type="submit">
                {this.state.label.save}
              </Button>
            </Form>
          ) : (
            <Game userId={1} />
          )}
        </div>
        <footer className="App-footer" />
      </div>
    );
  }
}

export default App;
